import type { Express, Request, Response, NextFunction } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertPropertySchema, insertBookingSchema, insertReviewSchema, insertDeliveryOrderSchema, insertUserSchema } from "@shared/schema";
import bcrypt from "bcrypt";
import jwt from "jsonwebtoken";
import { z } from "zod";

// Authentication validation schemas
const signUpSchema = z.object({
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  email: z.string().email("Please enter a valid email address").transform(email => email.toLowerCase().trim()),
  password: z.string().min(6, "Password must be at least 6 characters"),
  isHost: z.boolean().default(false),
});

const signInSchema = z.object({
  email: z.string().email("Please enter a valid email address").transform(email => email.toLowerCase().trim()),
  password: z.string().min(1, "Password is required"),
});

// Secure JWT secret management
const JWT_SECRET = process.env.JWT_SECRET;
if (!JWT_SECRET && process.env.NODE_ENV === 'production') {
  throw new Error('JWT_SECRET environment variable is required in production');
}
const jwtSecret = JWT_SECRET || "perra-dev-secret-not-for-production";

// Extend Request type to include user info
interface AuthenticatedRequest extends Request {
  user?: {
    userId: string;
    email: string;
    isHost: boolean;
  };
}

// Authentication middleware to verify JWT tokens
const authenticateToken = async (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
  try {
    const token = req.cookies['auth-token'];

    if (!token) {
      return res.status(401).json({ 
        code: "UNAUTHORIZED", 
        message: "Authentication required" 
      });
    }

    // Verify JWT token
    const decoded = jwt.verify(token, jwtSecret) as { 
      userId: string; 
      email: string; 
      isHost: boolean; 
    };

    // Verify user still exists
    const user = await storage.getUser(decoded.userId);
    if (!user) {
      return res.status(401).json({ 
        code: "UNAUTHORIZED", 
        message: "User not found" 
      });
    }

    // Attach user info to request
    req.user = {
      userId: decoded.userId,
      email: decoded.email,
      isHost: decoded.isHost
    };

    next();
  } catch (error) {
    return res.status(401).json({ 
      code: "UNAUTHORIZED", 
      message: "Invalid or expired token" 
    });
  }
};

// Authorization middleware for hosts only
const requireHost = (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
  if (!req.user?.isHost) {
    return res.status(403).json({ 
      code: "FORBIDDEN", 
      message: "Host privileges required" 
    });
  }
  next();
};

// Authorization middleware for guests only
const requireGuest = (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
  if (req.user?.isHost) {
    return res.status(403).json({ 
      code: "FORBIDDEN", 
      message: "Guest privileges required" 
    });
  }
  next();
};

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication routes
  app.post("/api/users", async (req, res) => {
    try {
      // Validate input data
      const validatedInput = signUpSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(validatedInput.email);
      if (existingUser) {
        return res.status(400).json({ 
          code: "USER_EXISTS", 
          message: "An account with this email already exists" 
        });
      }

      // Hash password
      const saltRounds = 12;
      const hashedPassword = await bcrypt.hash(validatedInput.password, saltRounds);

      // Create user data
      const userData = {
        email: validatedInput.email,
        password: hashedPassword,
        firstName: validatedInput.firstName,
        lastName: validatedInput.lastName,
        username: validatedInput.email,
        isHost: validatedInput.isHost,
      };

      const validatedUserData = insertUserSchema.parse(userData);
      const user = await storage.createUser(validatedUserData);

      // Don't return password in response
      const { password: _, ...userWithoutPassword } = user;
      res.status(201).json(userWithoutPassword);
    } catch (error) {
      console.error("User creation error:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          code: "VALIDATION_ERROR", 
          message: "Invalid input data", 
          errors: error.errors 
        });
      }
      res.status(500).json({ 
        code: "INTERNAL_ERROR", 
        message: "Failed to create user" 
      });
    }
  });

  app.post("/api/signin", async (req, res) => {
    try {
      // Validate input data
      const validatedInput = signInSchema.parse(req.body);

      // Find user by email
      const user = await storage.getUserByEmail(validatedInput.email);
      if (!user) {
        return res.status(401).json({ 
          code: "INVALID_CREDENTIALS", 
          message: "Invalid email or password" 
        });
      }

      // Verify password
      const isPasswordValid = await bcrypt.compare(validatedInput.password, user.password);
      if (!isPasswordValid) {
        return res.status(401).json({ 
          code: "INVALID_CREDENTIALS", 
          message: "Invalid email or password" 
        });
      }

      // Generate JWT token
      const token = jwt.sign(
        { 
          userId: user.id, 
          email: user.email, 
          isHost: user.isHost 
        },
        jwtSecret,
        { expiresIn: '7d' }
      );

      // Set secure cookie
      res.cookie('auth-token', token, {
        httpOnly: true,
        secure: process.env.NODE_ENV === 'production',
        sameSite: 'strict',
        maxAge: 7 * 24 * 60 * 60 * 1000 // 7 days
      });

      // Don't return password or token in response (use httpOnly cookie only)
      const { password: _, ...userWithoutPassword } = user;
      res.json({
        user: userWithoutPassword
      });
    } catch (error) {
      console.error("Sign in error:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          code: "VALIDATION_ERROR", 
          message: "Invalid input data", 
          errors: error.errors 
        });
      }
      res.status(500).json({ 
        code: "INTERNAL_ERROR", 
        message: "Authentication failed" 
      });
    }
  });

  // Sign out route
  app.post("/api/signout", (req, res) => {
    res.clearCookie('auth-token', {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      sameSite: 'strict'
    });
    res.json({ message: "Signed out successfully" });
  });

  // Properties routes
  app.get("/api/properties", async (req, res) => {
    try {
      const filters = {
        location: req.query.location as string,
        minPrice: req.query.minPrice ? parseInt(req.query.minPrice as string) : undefined,
        maxPrice: req.query.maxPrice ? parseInt(req.query.maxPrice as string) : undefined,
        amenities: req.query.amenities ? (req.query.amenities as string).split(',') : undefined
      };

      const properties = await storage.getProperties(filters);
      res.json(properties);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch properties" });
    }
  });

  app.get("/api/properties/:id", async (req, res) => {
    try {
      const property = await storage.getProperty(req.params.id);
      if (!property) {
        return res.status(404).json({ message: "Property not found" });
      }
      res.json(property);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch property" });
    }
  });

  app.post("/api/properties", async (req, res) => {
    try {
      console.log("Received data:", JSON.stringify(req.body, null, 2));
      const validatedData = insertPropertySchema.parse(req.body);
      console.log("Validated data:", JSON.stringify(validatedData, null, 2));
      const property = await storage.createProperty(validatedData);
      console.log("Created property:", JSON.stringify(property, null, 2));
      res.status(201).json(property);
    } catch (error) {
      console.error("Property creation error:", error);
      console.error("Error details:", error instanceof Error ? error.message : String(error));
      res.status(400).json({ message: "Invalid property data", error: error instanceof Error ? error.message : String(error) });
    }
  });

  app.get("/api/properties/:id/reviews", async (req, res) => {
    try {
      const reviews = await storage.getReviewsByProperty(req.params.id);
      res.json(reviews);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch reviews" });
    }
  });

  // Bookings routes
  app.post("/api/bookings", async (req, res) => {
    try {
      const validatedData = insertBookingSchema.parse(req.body);
      const booking = await storage.createBooking(validatedData);
      res.status(201).json(booking);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          code: "VALIDATION_ERROR", 
          message: "Invalid booking data", 
          errors: error.errors 
        });
      }
      res.status(400).json({ message: "Invalid booking data", error: error instanceof Error ? error.message : String(error) });
    }
  });

  app.get("/api/bookings/guest/:guestId", async (req, res) => {
    try {
      const bookings = await storage.getBookingsByGuest(req.params.guestId);
      res.json(bookings);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch bookings" });
    }
  });

  app.patch("/api/bookings/:id/status", async (req, res) => {
    try {
      const { status } = req.body;
      const booking = await storage.updateBookingStatus(req.params.id, status);
      if (!booking) {
        return res.status(404).json({ message: "Booking not found" });
      }
      res.json(booking);
    } catch (error) {
      res.status(500).json({ message: "Failed to update booking" });
    }
  });

  // Reviews routes
  app.post("/api/reviews", async (req, res) => {
    try {
      const validatedData = insertReviewSchema.parse(req.body);
      const review = await storage.createReview(validatedData);
      res.status(201).json(review);
    } catch (error) {
      res.status(400).json({ message: "Invalid review data" });
    }
  });

  // Delivery routes
  app.post("/api/delivery-orders", async (req, res) => {
    try {
      const validatedData = insertDeliveryOrderSchema.parse(req.body);
      const order = await storage.createDeliveryOrder(validatedData);
      res.status(201).json(order);
    } catch (error) {
      res.status(400).json({ message: "Invalid delivery order data" });
    }
  });

  app.get("/api/delivery-orders/guest/:guestId", async (req, res) => {
    try {
      const orders = await storage.getDeliveryOrdersByGuest(req.params.guestId);
      res.json(orders);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch delivery orders" });
    }
  });

  app.patch("/api/delivery-orders/:id/status", async (req, res) => {
    try {
      const { status } = req.body;
      const order = await storage.updateDeliveryOrderStatus(req.params.id, status);
      if (!order) {
        return res.status(404).json({ message: "Delivery order not found" });
      }
      res.json(order);
    } catch (error) {
      res.status(500).json({ message: "Failed to update delivery order" });
    }
  });


  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, properties, bookings, reviews, deliveryOrders,
  type User, type InsertUser,
  type Property, type InsertProperty,
  type Booking, type InsertBooking,
  type Review, type InsertReview,
  type DeliveryOrder, type InsertDeliveryOrder
} from "@shared/schema";
import { randomUUID } from "crypto";
import { db } from "./db";
import { eq } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Property operations
  getProperty(id: string): Promise<Property | undefined>;
  getProperties(filters?: {
    location?: string;
    minPrice?: number;
    maxPrice?: number;
    amenities?: string[];
  }): Promise<Property[]>;
  getPropertiesByHost(hostId: string): Promise<Property[]>;
  createProperty(property: InsertProperty): Promise<Property>;
  updateProperty(id: string, updates: Partial<InsertProperty>): Promise<Property | undefined>;

  // Booking operations
  getBooking(id: string): Promise<Booking | undefined>;
  getBookingsByGuest(guestId: string): Promise<Booking[]>;
  getBookingsByProperty(propertyId: string): Promise<Booking[]>;
  createBooking(booking: InsertBooking): Promise<Booking>;
  updateBookingStatus(id: string, status: string): Promise<Booking | undefined>;

  // Review operations
  getReviewsByProperty(propertyId: string): Promise<Review[]>;
  createReview(review: InsertReview): Promise<Review>;

  // Delivery operations
  getDeliveryOrdersByGuest(guestId: string): Promise<DeliveryOrder[]>;
  createDeliveryOrder(order: InsertDeliveryOrder): Promise<DeliveryOrder>;
  updateDeliveryOrderStatus(id: string, status: string): Promise<DeliveryOrder | undefined>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User> = new Map();
  private properties: Map<string, Property> = new Map();
  private bookings: Map<string, Booking> = new Map();
  private reviews: Map<string, Review> = new Map();
  private deliveryOrders: Map<string, DeliveryOrder> = new Map();

  constructor() {
    this.seedData();
  }

  // User operations
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = {
      ...insertUser,
      id,
      isHost: insertUser.isHost ?? false,
      createdAt: new Date(),
    };
    this.users.set(id, user);
    return user;
  }

  // Property operations
  async getProperty(id: string): Promise<Property | undefined> {
    return this.properties.get(id);
  }

  async getProperties(filters?: {
    location?: string;
    minPrice?: number;
    maxPrice?: number;
    amenities?: string[];
  }): Promise<Property[]> {
    let properties = Array.from(this.properties.values());

    if (filters) {
      if (filters.location) {
        properties = properties.filter(p => 
          p.location.toLowerCase().includes(filters.location!.toLowerCase())
        );
      }
      if (filters.minPrice !== undefined) {
        properties = properties.filter(p => 
          parseFloat(p.monthlyPrice) >= filters.minPrice!
        );
      }
      if (filters.maxPrice !== undefined) {
        properties = properties.filter(p => 
          parseFloat(p.monthlyPrice) <= filters.maxPrice!
        );
      }
      if (filters.amenities && filters.amenities.length > 0) {
        properties = properties.filter(p => 
          filters.amenities!.every(amenity => p.amenities?.includes(amenity))
        );
      }
    }

    return properties;
  }

  async getPropertiesByHost(hostId: string): Promise<Property[]> {
    return Array.from(this.properties.values()).filter(p => p.hostId === hostId);
  }

  async createProperty(insertProperty: InsertProperty): Promise<Property> {
    const id = randomUUID();
    const property: Property = {
      ...insertProperty,
      id,
      rating: "0.00",
      reviewCount: 0,
      createdAt: new Date(),
      images: insertProperty.images ?? [],
      amenities: insertProperty.amenities ?? [],
      hasStableElectricity: insertProperty.hasStableElectricity ?? true,
      hasKitchen: insertProperty.hasKitchen ?? true,
      hasWorkspace: insertProperty.hasWorkspace ?? false,
      hasAC: insertProperty.hasAC ?? false,
      hasCoffeeMachine: insertProperty.hasCoffeeMachine ?? false,
      isVerified: insertProperty.isVerified ?? false,
      internetSpeed: insertProperty.internetSpeed ?? null
    };
    this.properties.set(id, property);
    return property;
  }

  async updateProperty(id: string, updates: Partial<InsertProperty>): Promise<Property | undefined> {
    const property = this.properties.get(id);
    if (!property) return undefined;

    const updated = { ...property, ...updates };
    this.properties.set(id, updated);
    return updated;
  }

  // Booking operations
  async getBooking(id: string): Promise<Booking | undefined> {
    return this.bookings.get(id);
  }

  async getBookingsByGuest(guestId: string): Promise<Booking[]> {
    return Array.from(this.bookings.values()).filter(b => b.guestId === guestId);
  }

  async getBookingsByProperty(propertyId: string): Promise<Booking[]> {
    return Array.from(this.bookings.values()).filter(b => b.propertyId === propertyId);
  }

  async createBooking(insertBooking: InsertBooking): Promise<Booking> {
    const id = randomUUID();
    const booking: Booking = {
      ...insertBooking,
      id,
      depositRefunded: false,
      createdAt: new Date(),
      status: insertBooking.status ?? "pending"
    };
    this.bookings.set(id, booking);
    return booking;
  }

  async updateBookingStatus(id: string, status: string): Promise<Booking | undefined> {
    const booking = this.bookings.get(id);
    if (!booking) return undefined;

    const updated = { ...booking, status };
    this.bookings.set(id, updated);
    return updated;
  }

  // Review operations
  async getReviewsByProperty(propertyId: string): Promise<Review[]> {
    return Array.from(this.reviews.values()).filter(r => r.propertyId === propertyId);
  }

  async createReview(insertReview: InsertReview): Promise<Review> {
    const id = randomUUID();
    const review: Review = { 
      ...insertReview, 
      id, 
      createdAt: new Date(),
      comment: insertReview.comment ?? null
    };
    this.reviews.set(id, review);

    // Update property rating
    const reviews = await this.getReviewsByProperty(insertReview.propertyId);
    const avgRating = reviews.reduce((sum, r) => sum + r.rating, 0) / reviews.length;
    const property = this.properties.get(insertReview.propertyId);
    if (property) {
      const updated = {
        ...property,
        rating: avgRating.toFixed(2),
        reviewCount: reviews.length
      };
      this.properties.set(insertReview.propertyId, updated);
    }

    return review;
  }

  // Delivery operations
  async getDeliveryOrdersByGuest(guestId: string): Promise<DeliveryOrder[]> {
    return Array.from(this.deliveryOrders.values()).filter(o => o.guestId === guestId);
  }

  async createDeliveryOrder(insertOrder: InsertDeliveryOrder): Promise<DeliveryOrder> {
    const id = randomUUID();
    const order: DeliveryOrder = { 
      ...insertOrder, 
      id, 
      createdAt: new Date(),
      status: insertOrder.status ?? "pending",
      items: insertOrder.items ?? [],
      discountAmount: insertOrder.discountAmount ?? "0.00"
    };
    this.deliveryOrders.set(id, order);
    return order;
  }

  async updateDeliveryOrderStatus(id: string, status: string): Promise<DeliveryOrder | undefined> {
    const order = this.deliveryOrders.get(id);
    if (!order) return undefined;

    const updated = { ...order, status };
    this.deliveryOrders.set(id, updated);
    return updated;
  }

